#Formatação com f-strings

var = 'ABC'
print(f'{var}' )
print( f'{var: >10}')                                                                                                                                                                                                                                               
print( f'{var: >10}')                                                                                                                                                                                                                                               
print( f'{var: ^10}')
print(f'{1000.56784485674568:0=+10,.1f}')                                                                                                                                                                                                                                            
print(f' o hexadecimal de 1500 é{1500:04X}')
print(f'{var!r}')                                                                                                                                                                                                                                            
